import React from 'react';

const Recipes = () => {
    return (
        <div>
            { this.state.recipes.map((recipe) => {
                return (
                    <div  key={recipe.recipe_id}>
                        <img src={recipe.image_url} alt={recipe.title}/>
                        <p >{ recipe.title }</p>
                    </div>
                );
            }) }
        </div>
    );
};

export default Recipes;
