import React, {Component} from 'react';

import { Link } from "react-router-dom";

const API_KEY = "28cab5d3d8224a9f7450d2358b835fdc";

class Recipe extends Component {
    state = {
        activeRecipe: []
    }

    componentDidMount = async () => {
        const title = this.props.location.state.recipe;
        const req = await fetch(`https://cors-anywhere.herokuapp.com/http://food2fork.com/api/search?key=${API_KEY}&q=${title}`);

        const res = await req.json();
        this.setState({ activeRecipe: res.recipes[0] });
        console.log(this.state.activeRecipe);
    }

    render() {
        const recipe = this.state.activeRecipe;
        return (
            <div className="container">
                { this.state.activeRecipe.length !== 0 &&
                <div className="active-recipe">
                    <img  className="active-recipe__img" src={recipe.image_url} alt={recipe.title}/>
                    <h3 className="active-recipe__title">{ recipe.title }</h3>
                    <h4 className="active-recipe__publisher">
                        publisher: <span>{recipe.publisher }</span>
                    </h4>
                    <p className="active-recipe__website">website: <span><a href={recipe.publisher_url}>{recipe.publisher_url}</a></span>
                    </p>
                    <button className="active-recipe__button">
                        <Link to="/">Go Home</Link>
                    </button>
                </div>
                }
            </div>
        );
    }
}

export default Recipe;
