import React from 'react';

const Recipes2 = (props) => {
    return (
        <div>
            { props.recipes.map((recipe) => {
                return(
                    <div key={recipe.title} className="col-md" style={{ marginBottom:"2rem" }}>
                        <div className="recipes__box">
                            <img
                                className="recipe__box-img"
                                src={recipe.image_url}
                                alt={ recipe.title }/>
                            <div className="recipe__text">
                                <h5 className="recipes__title">{ recipe.title }</h5>
                                <p className="recipes__subtitle">publisher: <span> { recipe.publisher } </span></p>
                            </div>
                            <button className="recipe_buttons">
                                View Recipe
                            </button>
                        </div>

                    </div>
                );
            }) }
        </div>
    );
};

export default Recipes2;
